import React, { Component, useContext } from 'react';
import {StyleSheet, Text, View, Pressable, ScrollView} from 'react-native';
import ReaderContext from "../context";
import {useNavigation} from "@react-navigation/native";
import {useQuery} from "react-apollo";
import {getWordsQuery} from "./queries";

const Reader = ({navigation}) => {
    // const context = useContext(ReaderContext);
    let sortedData = [];
    let words;
    const context = {
        bookName: 'Romans',
        referenceFrom: '060101',
        referenceTo: '060102'
    }
    const bookName = context.bookName;
    const chapterFrom = context.referenceFrom.slice(2,4).replace('0', '');
    const chapterTo = context.referenceFrom.slice(4).replace('0', '');
    const verseFrom = context.referenceTo.slice(2,4).replace('0', '');
    const verseTo = context.referenceTo.slice(4).replace('0', '');

    const {loading, error, data } = useQuery(getWordsQuery, {
        variables: {
            referenceFrom: context.referenceFrom,
            referenceTo: context.referenceTo
        }
    });

    const onWordTouch = (e, id) => {
        console.log(e);
        console.log(id);
    }

    if(data) {
        console.log(data.words);
        sortedData = [... data.words];
        sortedData.sort((a,b) => {
            return a.id - b.id
        });
        console.log(sortedData);
        words = sortedData.map(word => (
            <View key={word.id}>
                <Pressable onPress={(e, id) => onWordTouch(e, id)}>
                    <Text style={styles.word}>{word.text}</Text>
                </Pressable>
            </View>
        ));

    }

    if (loading) return <View><Text>Loading...</Text></View>;
    if (error) return <View><Text>Error! {error.message}</Text></View>;

    return (

        <View style={styles.container}>
            <View style={styles.titleContainer}>
                <Text style={styles.titleText}>{bookName} {chapterFrom}:{verseFrom} - {chapterTo}:{verseTo}</Text>
            </View>
            <View style={styles.viewContainer}>
                <ScrollView contentContainerStyle={styles.viewer}>
                    {words}
                </ScrollView>
            </View>

            <View style={styles.infoBoxContainer}>
                <Text>Morphological info here</Text>
            </View>
        </View>

    );
}

const styles = StyleSheet.create({
    container: {
        display: "flex",
        flex: 1,
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
    },
    titleContainer: {
       flex: 1,
       alignItems: "center",
       justifyContent: "center",
    },
    titleText: {
        fontWeight: "bold",
        fontSize: 30,
        color: "#1d78c1"
    },
    viewContainer: {
        flex: 3
    },
    viewer: {
        display: "flex",
        flexDirection: "row",
        flexWrap: "wrap",
        margin: 20
    },
    infoBoxContainer: {
        width: "96%",
        flex: 1,
        borderStyle: "solid",
        borderWidth: 1,
        borderColor: "#CCCCCC",
        borderRadius: 10,
        margin: 20,
        padding: 20
    },
    infoBox: {

    },
    wordInfo: {

    },
    posInfo: {

    },
    parseInfo: {

    },
    glossInfo: {

    },
    word: {
        margin: 2,
        borderRadius: 5,
        backgroundColor: "#EEEEEE",
        padding: 5
    },
    wordText: {
        padding: 5
    }
});

// export default function(props) {
//     const navigation = useNavigation();
//     return <Reader {...props} navigation={navigation} />
// }

export default Reader;
